property.code:
require.config({
  paths: {
    'xwiki-selectize': "$xwiki.getSkinFile('uicomponents/suggest/xwiki.selectize.js', true)" +
      "?v=$escapetool.url($xwiki.version)"
  }
});
define('xwiki-suggestPagesPro', ['jquery', 'xwiki-selectize'], function ($) {
  const reference = XWiki.Model.resolve('XWikiProCommons.Pickers.PagePicker', XWiki.EntityType.DOCUMENT);
  const URL = new XWiki.Document(reference).getURL('get');
  var getSettings = function (select) {
    return {
      load: function (typedText, callback) {
        $.getJSON(URL, {
          page: typedText,
        }).done(callback).fail(callback);
      },
      loadSelected: function (selectedValue, callback) {
        $.getJSON(URL, {
          page: selectedValue,
          exactMatch: true
        }).done(callback).fail(callback);
      }
    };
  };

  $.fn.suggestPages = function (settings) {
    return this.each(function () {
      $(this).xwikiSelectize($.extend(getSettings($(this)), settings));
    });
  };
});

require(['jquery', 'xwiki-suggestPagesPro', 'xwiki-events-bridge'], function ($) {
  var init = function (event, data) {
    var container = $((data && data.elements) || document);
    container.find('.suggest-pages').suggestPages();
  };

  $(document).on('xwiki:dom:loaded xwiki:dom:updated', init);
  XWiki.domIsLoaded && init();
});
